import { ActivatedRoute, Router } from '@angular/router';
import { PensamentoService } from './../pensamento.service';
import { Pensamento } from './../pensamento';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-excluir-pensamento',
  templateUrl: './excluir-pensamento.component.html',
  styleUrls: ['./excluir-pensamento.component.css']
})
export class ExcluirPensamentoComponent implements OnInit{

  pensamento: Pensamento = {
    id: 0,
    conteudo: "",
    autoria: "",
    modelo: ""
  }

  constructor(
    private service: PensamentoService,
    private router: Router,
    private route: ActivatedRoute
  ){}

    ngOnInit(): void {
        const id = this.route.snapshot.paramMap.get("id");
        this.service.buscarPorId(parseInt(id!)).subscribe((pensamento)=>{
          this.pensamento = pensamento
        })
    }

    excluirPensamento(){
      if(this.pensamento.id){
        this.service.excluir(this.pensamento.id).subscribe(()=>{
          this.router.navigate(['/listar-pensamento'])
        })
      }
    }

}
<section class="container excluir-pensamentos ff-inter">
    <div class="modal">
        <p>O pensamento será deletado <br>Confirma a exclusão?</p>
        <div class="acoes">
            <button class="botao botao-excluir" (click)="exc">Excluir</button>
            <button  class="botao botao-cancelar">Cancelar</button>
        </div>
    </div>
    <div class="overlay"></div>
</section>
