import { Pensamento } from './../pensamento';
import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-pensamento',
  templateUrl: './pensamento.component.html',
  styleUrls: ['./pensamento.component.css']
})
export class PensamentoComponent {

  @Input() pensamento: Pensamento = {
    id:0,
    conteudo: 'teste',
    autoria: 'aninha',
    modelo: 'modelo1'
  }

  larguraPensamento(): string{
    if(this.pensamento.conteudo.length >= 256){
      return 'pensamento-g'
    }else{
      return 'pensamento-p'
    }
  }

}
<div class="pensamento {{pensamento.modelo}} ff-roboto-mono [[ngClass]='larguraPensamento()'">
    <img src="../../../../assets/imagens/{{pensamento.modelo}}.png" alt="aspas coloridas">
    <p class="conteudo">{{pensamento.conteudo}}</p>
    <p class="autoria"> <b>{{pensamento.autoria}}</b></p>
    <div class="acoes">
        <button class="botao-editar">
            <img src="../../../../assets/imagens/icone-editar.png" alt="ícone editar">
        </button>
        <button class="botao-excluir" routerLink="/pensamentos/excluir-pensamento/{{pensamento.id}}">
            <img src="../../../../assets/imagens/icone-excluir.png" alt="ícone excluir">
        </button>
    </div>
</div>
